mapper.select:
select *
from reply where bno = #{bno}
order by rno desc

mapper.update:
update reply
set replytext = #{replytext}, updatedate=now()
where rno = #{rno}

mapper.select:
select *
from reply
where bno = #{bno}
order by rno desc
limit #{cri.pageStart}, #{cri.perPageNum}

select count(bno)
from reply
where bno = #{bno}
